import { useEffect } from "react";
import { useSelector } from "react-redux";

import decodeFEN from "./FENtoBoard";

import useAction, { setBoardAction } from "./actions";
import { selectStringFEN } from "./slices/game";

import Grid from "@mui/material/Grid";
import Square from "./Square";


const ChessBoard = () => {

  const stringFEN = useSelector(selectStringFEN);
  const setBoard = useAction(setBoardAction);

  useEffect(() => {
    // console.log(stringFEN);
    // return;
    setBoard(decodeFEN(stringFEN));
  }, [stringFEN, setBoard]);

  return (
    <Grid container spacing={0} id="board" draggable="false" >
      {Array.from(new Array(8)).map((_, rank) => (
        <Grid key={`${rank}`} container item xs={12} spacing={0} direction="row-reverse" >
          {rank + 1}
          {Array.from(new Array(8)).map((_, file) => (
            <Grid key={`${file}`} item xs={1} >
              <Square index={rank * 8 + file} />
              {rank === 7 ? file + 1 :""}
            </Grid>
          ))}
        </Grid>
      ))}
    </Grid>
  );
};

export default ChessBoard;
