import { useState, useEffect } from "react";
import { useSelector } from "react-redux";

import {
  selectActivePiece,
  selectLegalMoves,
  selectTurn,
  selectFen,
} from "./slices/game";
import useAction, {
  setActivePieceAction,
  setLegalMovesAction,
  rotateTurnAction,
  setFenAction,
} from "./actions";

import pieces, { pieceTypes } from "./pieces";
import calculateLegalMovesForPiece, {
  calculateLegalMoves,
  isKingAttacked,
} from "./calculateMoves";

import { movePiece } from "./utils";

export default function Piece({ piece: initial, index }) {
  // const board = useSelector(selectBoard);
  const fen = useSelector(selectFen);
  const activePiece = useSelector(selectActivePiece);
  const setActivePiece = useAction(setActivePieceAction);

  const legalMoves = useSelector(selectLegalMoves);
  const setLegalMoves = useAction(setLegalMovesAction);

  const turn = useSelector(selectTurn);
  const rotateTurn = useAction(rotateTurnAction);

  const setFen = useAction(setFenAction);

  const [color, setColor] = useState(0b10000 & initial ? "w" : "b");
  const [type, setType] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );

  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [offset, setOffset] = useState({ x: 0, y: 0 });

  useEffect(() => {
    setColor(0b10000 & initial ? "w" : "b");
    setType(Object.keys(pieces).find((key) => pieces[key] === initial));
  }, [initial]);

  const handleMouseDown = (event) => {
    // console.log(turn, color);
    event.preventDefault();
    setActivePiece(index);
    setIsDragging(true);

    const piece = event.target.getBoundingClientRect();
    setOffset({
      x: piece.left + piece.width / 2,
      y: piece.top + piece.height / 2,
    });
    setPosition({
      x: event.clientX - piece.left - piece.width / 2,
      y: event.clientY - piece.top - piece.height / 2,
    });

    // console.log("FEN PIECES", FEN);
    setLegalMoves(calculateLegalMovesForPiece(fen, index));
  };

  const handleMouseMove = (event) => {
    if (!isDragging) return;
    setPosition({
      x: -offset.x + event.clientX,
      y: -offset.y + event.clientY,
    });
  };

  const handleMouseUp = (event) => {
    setIsDragging(false);
    const boardRect = document.getElementById("board").getBoundingClientRect();
    const rank = Math.floor((event.clientY - boardRect.top) / 70);
    const file = Math.floor((event.clientX - boardRect.left) / 70);
    const to = rank * 8 + file;

    if (turn !== color) {
      premove();
      setPosition({ x: 0, y: 0 });
      return;
    }

    if (!legalMoves.includes(to)) {
      setPosition({ x: 0, y: 0 });
      return;
    }
    playTurn(index, to);
  };

  function premove() {
    console.log("premove");
  }

  function playTurn(from, to) {
    let board = movePiece(fen.board, from, to);
    let castling = fen.castling;
    let enPassant = "";
    let halfMoveClock = fen.halfMoveClock + 1;
    let fullMoveNumber = fen.fullMoveNumber + (color === "b" ? 1 : 0);
    const capturedPiece = fen.board[to] & 0b111;
    const rank = Math.floor(to / 8);
    const file = to % 8;
    console.log( Object.keys(pieces))
    const type = Object.keys(pieces).find((el) => el === (fen.board[from]& 0b10111)); 
    console.log(type);
    const notation = `${type}${capturedPiece ? "x" :""}${"abcdefgh"[file]}${8 - rank}`;
    console.log(notation);

    // castling

    let toRemove = [];
    if (type === pieceTypes.King) {
      if (color === "w") toRemove += "KQ";
      else toRemove += "kq";
      if (to === from + 2) board = movePiece(fen.board, from + 3, from + 1);
      else if (to === from - 2)
        board = movePiece(fen.board, from - 4, from - 1);
    } else if (type === pieceTypes.Rook) {
      if (color === "w") {
        if (from === 7) toRemove += "K";
        else if (from === 0) toRemove += "Q";
      } else {
        if (from === 63) toRemove += "k";
        else if (from === 56) toRemove += "q";
      }
    }

    // en passant
    else if (type === pieceTypes.Pawn) {
      halfMoveClock = 0;
      if (color === "w") {
        if (to - from === 16) enPassant = to - 8;
        else if (to - from === -16) enPassant = to + 8;
        if (fen.enPassant === to) board[to - 8] = 0;
      } else {
        if (to - from === 16) enPassant = to - 8;
        else if (to - from === -16) enPassant = to + 8;
        if (fen.enPassant === to) board[to + 8] = 0;
      }
    }

    [...toRemove].forEach((el) => (castling = castling.replace(el, "")));

    const newFen = {
      ...fen,
      board,
      castling,
      enPassant,
      halfMoveClock,
      fullMoveNumber,
    };
    setFen(newFen);

    if (checkGameOver(newFen)) {
      console.log("GAME OVER");
      return;
    }
    rotateTurn();
  }

  return (
    <img
      style={{ left: position.x, top: position.y }}
      className={"piece" + (activePiece === index ? " active" : "")}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      src={`pieces/${color?.toLowerCase()}${type?.toUpperCase()}.svg`}
      alt="Chess Piece"
    />
  );
}

function checkGameOver(fen) {
  const board = fen.board;
  const turn = fen.turn;
  const color = turn === "w" ? 0b1000 : 0b10000;
  const king = color | pieceTypes.King;
  const kingIndex = board.indexOf(king);
  const legalKingMoves = calculateLegalMovesForPiece(fen, kingIndex);
  // console.log(legalMoves, isKingAttacked(fen, kingIndex));
  return (
    legalKingMoves.length === 0 &&
    isKingAttacked(fen, kingIndex) &&
    calculateLegalMoves(fen, color).length === 0
  );
}
