import Grid from "@mui/material/Grid";

import { useState } from "react";

import FENtoBoard from "./FENtoBoard";
import Square from "./Square";

import { useSelector } from "react-redux";
import { selectBoard } from "./slices/game";

import {useCallback} from "react";
import {useDispatch} from "react-redux";

const setBoardAction = (board) => {
  console.log(board);
  return {
    type: "game/setBoard",
    payload: board,
  };
};

const useAction = (action) => {
  const dispatch = useDispatch();
  return useCallback((...args) => dispatch(action(...args)), [dispatch]);
};


const ChessBoard = () => {

  const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");
  const [activePiece, setActivePiece] = useState(null);
  const [board, setBoard] = useState([...FENtoBoard(FEN)]);
  useAction(setBoardAction([...FENtoBoard(FEN)]));

  const boardRedux = useSelector(selectBoard);
  console.log(boardRedux)



  return (
    <Grid container spacing={0} id ="board" draggable="false">
      {Array.from(new Array(8)).map((_, rank) => (
        <Grid key={`${rank}`} container item xs={12} spacing={0}>
          {Array.from(new Array(8)).map((_, file) => (
            <Grid key={`${file}`} item xs={1}>
              <Square
                rank={rank}
                file={file}
                setActivePiece={setActivePiece}
                activePiece={activePiece}
                setBoard = {setBoard}
                board={board}
              />
            </Grid>
          ))}
        </Grid>
      ))}
    </Grid>
  );
};

export default ChessBoard;
