import Paper from "@mui/material/Paper";
import Piece from "./Piece";

const Square = ({ rank, file, board, activePiece, setActivePiece }) => {

    const handleDragOver = (event) => {
        event.preventDefault();
        event.target.classList.addClass("dragOver")
    };
    
  return (
    <Paper
      style={{
        position: "relative",
        height: "70px",
        width: "70px",
        backgroundColor: (rank + file) % 2 ? "#eeeed2" : "#769656",
      }}
      onDragEnter={handleDragOver}
    >
      {board[rank * 8 + file] !== 0 && (
        <Piece
          piece={board[rank * 8 + file]}
          setActivePiece={setActivePiece}
          activePiece={activePiece}
          index={rank * 8 + file}
        />
      )}
    </Paper>
  );
};

export default Square;
