import "./App.css";
import { useState } from "react";
import Box from "@mui/material/Box";
// import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import CssBaseline from "@mui/material/CssBaseline";
import Pieces from "./Pieces";

const pieces = {
  king: "♔",
  queen: "♕",
  rook: "♖",
  bishop: "♗",
  knight: "♘",
  pawn: "♙",
};

function App() {
  const [FEN] = useState("rnbqkbrn/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");

  return (
    <Container>
      <Pieces />
      <CssBaseline />
      {
        Array.from(new Array(64)).map((_, index) => {
          return <span key={index}>{index}</span>
        })
      }
    </Container>
  );
}

export default App;

function Piece(type) {

  const handleDrag = (event) => {
    console.log(event.target.getBoundingClientRect());

  } 
  return (
    <div className="piece" onDrag={handleDrag}>
      {
        {
          k: <King />,
          K: <King />,
          q: <Queen />,
          Q: <Queen />,

          r: <Rook />,
          R: <Rook />,
          b: <Bishop />,
          B: <Bishop />,
          n: <Knight />,
          N: <Knight />,
          p: <Pawn />,
          P: <Pawn />,
        }[type.type]
      }
    </div>
  );
}

function Knight() {
  return <span>{pieces.knight}</span>;
}

function Bishop() {
  return <span>{pieces.bishop}</span>;
}

function Rook() {
  return <span>{pieces.rook}</span>;
}

function Queen() {
  return <span>{pieces.queen}</span>;
}

function King() {
  return <span>{pieces.king}</span>;
}
function Pawn() {
  return <span>{pieces.pawn}</span>;
}
