    const color =  getPieceColor(piece)
    const pieceString = ["Pawn", "Knight", "Bishop", "Rook", "Queen", "King"][type - 1];
     return (color === pieceTypes.White ? "White " : "Black ") + pieceString.toLowerCase() + (index %8 ===0 ? "\n" : "");
  });
  console.log(boardString.join(""));
}

export {
