import useState from "react";

const pieces = {
    p: 1,
    n: 2,
    b: 3,
    r: 4,
    q: 5,
    k: 6,
    P: 7,
    N: 8,
    B: 9,
    R: 10,
    Q: 11,
    K: 12,
  };
  


function Piece({ piece, setActivePiece, index, activePiece }) {
  const [color] = useState(piece > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === piece)
  );

  return (
    <img
      className={"piece" + (activePiece == index ? " active" : "")}
      onMouseDown={() => setActivePiece(index)}
      src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
    ></img>
  );
}

const FENtoBoard = (FEN) => {
    const board = Array.from(64).fill(0);
    let file = 0;
    let rank = 7;
  
    [...FEN].forEach((symbol) => {
      if (symbol == "/") {
        file = 0;
        rank--;
      } else {
        if (!isNaN(parseInt(symbol, 10))) {
          file += parseInt(symbol, 10);
        } else {
          board[rank * 8 + file] = pieces[symbol];
          file++;
        }
      }
    });
    return board;
  };
