import pieces from "./pieces";

const calculateLegalMoves = (board, index) => {
  let legalMoves = [];

  const piece = board[index];
  const color = piece & 0b10000 ? "w" : "b";
  const type = Object.keys(pieces).find((key) => pieces[key] === piece);

  switch (type.toLowerCase()) {
    case "p":
      legalMoves = calculatePawnMoves(board, index);
      break;
    case "r":
      legalMoves = calculateRookMoves(board, index);
      break;
    case "n":
      legalMoves = calculateKnightMoves(board, index);
      break;
    case "b":
      legalMoves = calculateBishopMoves(board, index);
      break;
    case "q":
      legalMoves = calculateQueenMoves(board, index);
      break;
    case "k":
      legalMoves = calculateKingMoves(board, index);
      break;
  }
  return legalMoves;
};

function isOccupied(board, index) {
  return board[index] !== 0;
}

function isSameColor(board, index1, index2) {
  return (
    board[index1] & board[index2] & 0b10000 ||
    board[index1] & board[index2] & 0b1000
  );
}

function calculatePawnMoves(board, index) {
  let rank = Math.floor(index / 8);
  let file = index % 8;
  const legalMoves = [];
  const color = board[index] & 0b10000 ? "w" : "b";
  if (color === "b") {
    if (!isOccupied(board, index - 8)) legalMoves.push(index - 8);
    if (rank === 6 && !isOccupied(board, index - 16))
      legalMoves.push(index - 16);
    if (
      file > 0 &&
      isOccupied(board, index - 7) &&
      !isSameColor(board, index, index - 7)
    )
      legalMoves.push(index - 7);
    if (
      file < 7 &&
      isOccupied(board, index - 9) &&
      !isSameColor(board, index, index - 9)
    )
      legalMoves.push(index - 9);
  } else {
    legalMoves.push(index + 8);
    if (rank === 1 )
      legalMoves.push(index + 16);
    if (
      file > 0
    )
      legalMoves.push(index + 9);
    if (
      file < 7 &&
      isOccupied(board, index + 7) &&
      !isSameColor(board, index, index + 7)
    )
      legalMoves.push(index + 7);
  }
  return legalMoves;
}

function calculateRookMoves(board, index) {
  const candidateMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  for (let i = -1; i >= -file; i--) {
    candidateMoves.push(index + i);
  }
  for (let i = 1; i < 8 - file; i++) {
    candidateMoves.push(index + i);
  }
  for (let i = -1; i >= -rank; i--) {
    candidateMoves.push(index + 8 * i);
  }
  for (let i = 1; i < 8 - rank; i++) {
    candidateMoves.push(index + 8 * i);
  }
  const legalMoves = candidateMoves.filter(
    (move) => !isSameColor(board, index, move)
  );

  return legalMoves;
}

function calculateBishopMoves(board, index) {
  const candidateMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  for (let i = -1; i >= -file; i--) {
    candidateMoves.push(index + i + i * 8);
  }
  for (let i = 1; i < 8 - file; i++) {
    candidateMoves.push(index + i + i * 8);
  }
  for (let i = -1; i >= -rank; i--) {
    candidateMoves.push(index - i + 8 * i);
  }
  for (let i = 1; i < 8 - rank; i++) {
    candidateMoves.push(index + 8 * i - i);
  }
  const legalMoves = candidateMoves.filter(
    (move) => !isSameColor(board, index, move)
  );
  return legalMoves;
}

function calculateKnightMoves(board, index) {
  let candidateMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  if (file > 1) {
    if (rank > 0) candidateMoves.push(index - 17);
    if (rank < 7) candidateMoves.push(index + 15);
  }
  if (file > 0) {
    if (rank > 1) candidateMoves.push(index - 10);
    if (rank < 6) candidateMoves.push(index + 6);
  }
  if (file < 7) {
    if (rank > 1) candidateMoves.push(index - 6);
    if (rank < 6) candidateMoves.push(index + 10);
  }
  if (file < 6) {
    if (rank > 0) candidateMoves.push(index - 15);
    if (rank < 7) candidateMoves.push(index + 17);
  }

  let legalMoves = candidateMoves.filter(
    (move) => !isSameColor(board, index, move)
  );

  return legalMoves;
}

function calculateQueenMoves(board, index) {
  return [
    ...calculateBishopMoves(board, index),
    ...calculateRookMoves(board, index),
  ];
}

function calculateKingMoves(board, index) {
  let candidateMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  if (file > 0) {
    candidateMoves.push(index - 1);
    if (rank > 0) candidateMoves.push(index - 9);
    if (rank < 7) candidateMoves.push(index + 7);
  }
  if (file < 7) {
    candidateMoves.push(index + 1);
    if (rank > 0) candidateMoves.push(index - 7);
    if (rank < 7) candidateMoves.push(index + 9);
  }
  if (rank > 0) candidateMoves.push(index - 8);
  if (rank < 7) candidateMoves.push(index + 8);

  const legalMoves = candidateMoves.filter(
    (move) => !isSameColor(board, index, move)
  );
  return legalMoves;
}

export default calculateLegalMoves;
