import { useState } from "react";

import pieces from "./pieces";

const startingCoordinates =  {x: null, y: null}

export default function Piece({
  piece: initial,
  setActivePiece,
  index,
  activePiece,
}) {
  const [color] = useState(initial > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [offset, setOffset] = useState({ x: 0, y: 0 });


  // const handleDragStart = (ev) => {
  //   ev.preventDefault();
  //   setActivePiece(index);
  //   startingCoordinates.x = ev.pageX;
  //   startingCoordinates.y = ev.pageY;
  //   var img = new Image();
  //   img.src = 'data:image/gif;base64,R0lGODlhAQABAIAAAAUEBAAAACwAAAAAAQABAAACAkQBADs=';
  //   ev.dataTransfer.setDragImage(img, 0, 0);
  //   ev.dataTransfer.effectAllowed = "move";
  // }
  // const handleDrag = (ev) => {
  //   console.log(ev.pageX, ev.pageY)

  //   ev.target.style.transform = `translate(${ ev.pageX -startingCoordinates.x - (ev.target.width/2) }px, ${ev.pageY -startingCoordinates.y -(ev.target.height/2) }px)`;
  // }

  const handleDragEnd = (ev) => {
    console.log(ev);
    ev.target.style.transform = `translate(-50%, -50%)`;
  }

  const handleMouseDown = (event) => {
    event.preventDefault();
    setIsDragging(true);
    setOffset({
      x: event.clientX - position.x,
      y: event.clientY - position.y,
    });
  };

  const handleMouseMove = (event) => {
    if (!isDragging) return;
    setPosition({
      x: event.clientX - offset.x,
      y: event.clientY - offset.y,
    });
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  return (
      <img
      style={{  left: position.x, top: position.y }}
      draggable = "true"
        className={"piece" + (activePiece === index ? " active" : "")}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
        alt="Chess Piece"
      />
  );
}
