import "./App.css";
import { useState } from "react";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import Pieces from "./Pieces";

const None = 0;
const King = 1;
const Pawn = 2;
const Knight = 3;
const Bishop = 4;
const Rook = 5;
const Queen = 6;
const White = 8;
const Black = 16;

function App() {
  const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");

  const FENtoBoard = (FEN) => {
    const board = [];
    const rows = FEN.split(" ")[0];
    console.log(rows);
    let file = 0;
    let rank = 7;

    [...FEN].forEach((symbol) => {
      if (symbol == "/") {
        file = 0;
        rank--;
      } else {
        if (!isNaN(parseInt(symbol, 10))) {
          file += parseInt(symbol, 10);
        } else {
          console.log(symbol);
          board[rank * 8 + file] = symbol;
          file++;
        }
      }
    });
    // for (let i = 0; i < rows.length; i++) {
    //   for (let j = 0; j < rows[i].length; j++) {
    //     console.log(rows[i][j])
    //     if(!isNaN(parseInt(rows[i][j], 10))){
    //       j += parseInt(rows[i][j], 10);
    //       console.log("found int, adding " + parseInt(rows[i][j], 10) + " to j")
    //     }
    //   }
    // }
    return board;
  };

  console.log(FENtoBoard(FEN));

  return (
    <Container>
      <CssBaseline />
    </Container>
  );
}

export default App;

function Piece(type) {
  const handleDrag = (event) => {
    console.log(event.target.getBoundingClientRect());
  };
  return <div className="piece" onDrag={handleDrag}></div>;
}

