import { useState } from "react";

import pieces from "./pieces";

export default function Piece({ piece, setActivePiece, index, activePiece }) {
  const [color] = useState(piece > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === piece)
  );

  const handleDragStart = (event) => {

    setActivePiece(index);

    event.dataTransfer.setData("text/plain", index);
    event.dataTransfer.setData("text/html", event.currentTarget);
    event.dataTransfer.effectAllowed = "move";
    
  };



  return (
    <img
      draggable
      className={"piece" + (activePiece === index ? " active" : "")}
      onDragStart={handleDragStart}
      src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
      alt= "Chess Piece"
    />
  );
}
