import "./App.css";
import { useState } from "react";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import Pieces from "./Pieces";


const pieces = {
  p : 1,
  n : 2,
  b : 3,
  r : 4,
  q : 5,
  k : 6,
  P : 7,
  N : 8,
  B : 9,
  R : 10,
  Q : 11,
  K : 12,
}


function App() {
  const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");

  const FENtoBoard = (FEN) => {
    const board = Array.from(64).fill(0);
    let file = 0;
    let rank = 7;

    [...FEN].forEach((symbol) => {
      if (symbol == "/") {
        file = 0;
        rank--;
      } else {
        if (!isNaN(parseInt(symbol, 10))) {
          file += parseInt(symbol, 10);
        } else {
          console.log(symbol);
          board[rank * 8 + file] =  pieces[symbol];
          file++;
        }
      }
    });
    return board;
  };

  console.log(FENtoBoard(FEN));

  return (
    <Container>
      <CssBaseline />
      {
        <div className="board">
          {FENtoBoard(FEN).map((piece, index) => {
            return <Pieces key={index} piece={piece} />;
          }
          )}
        </div>
      }
    </Container>
  );
}

export default App;

function Piece(type) {
  const handleDrag = (event) => {
    console.log(event.target.getBoundingClientRect());
  };
  return <div className="piece" onDrag={handleDrag}></div>;
}

