const board = new Array(64).fill(0);


function App() {

  return (
    <Container>
      <CssBaseline />
      {
        Array.from(new Array(64)).fill(0).map((val, index) => {
          return <span key={index}>{val}</span>
        })
      }
    </Container>
  );
}

