import { useState, useEffect } from "react";

import pieces from "./pieces";

export default function Piece({
  piece: initial,
  setActivePiece,
  index,
  activePiece,
  setBoard,
  board,
}) {
  const [color, setColor] = useState(0b10000 & initial ? "w" : "b");
  const [type, setType] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [legalMoves, setLegalMoves] = useState([]);

  useEffect(() => {
    setColor(0b10000 & initial ? "w" : "b");
    setType(Object.keys(pieces).find((key) => pieces[key] === initial));
  }, [initial]);

  const handleMouseDown = (event) => {
    event.preventDefault();
    setActivePiece(index);
    setIsDragging(true);
    const piece = event.target.getBoundingClientRect();
    setOffset({
      x: piece.left + piece.width / 2,
      y: piece.top + piece.height / 2,
    });
    setLegalMoves(calculateLegalMoves(board, index))
  };

  const handleMouseMove = (event) => {
    if (!isDragging) return;

    setPosition({
      x: -offset.x + event.clientX,
      y: -offset.y + event.clientY,
    });
    const x = event.clientX;
    const y = event.clientY;
    const board = document.getElementById("board").getBoundingClientRect();
    const rank = Math.floor((y - board.top) / 70);
    const file = Math.floor((x - board.left) / 70);
    if (rank < 0 || rank > 7 || file < 0 || file > 7) {
      // setPosition({ x: 0, y: 0 });
      return;
    }
    const squares = document.getElementsByClassName("square");

    squares[rank * 8 + file].classList.add("dragover");
  };

  const handleMouseUp = (event) => {
    // event.target.style.cursor= "default"
    setIsDragging(false);

    const x = event.clientX;
    const y = event.clientY;
    const boardRect = document.getElementById("board").getBoundingClientRect();
    const rank = Math.floor((y - boardRect.top) / 70);
    const file = Math.floor((x - boardRect.left) / 70);
    const newElement = initial;

    if (rank < 0 || rank > 7 || file < 0 || file > 7) {
      setPosition({ x: 0, y: 0 });
      return;
    }
    if (rank * 8 + file === index) {
      setPosition({ x: 0, y: 0 });
      return;
    }

    if( !legalMoves.includes(rank * 8 + file)){
      setPosition({ x: 0, y: 0 });
      return;
    }
    setBoard((prev) => {
      return prev.map((element, i) => {
        if (index === i) return 0;
        if (i === rank * 8 + file) {
          return newElement;
        }
        return element;
      });
    });
  };

  return (
    <img
      style={{ left: position.x, top: position.y }}
      className={"piece" + (activePiece === index ? " active" : "")}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      // onDragStart = {handleDragStart}
      src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
      alt="Chess Piece"
    />
  );
}

const calculateLegalMoves = (board, index) => {
  let legalMoves = [];

  const piece = board[index];
  const color = piece & 0b10000 ? "w" : "b";
  const type = Object.keys(pieces).find((key) => pieces[key] === piece);

  switch (type.toLowerCase()) {
    case "p":
      legalMoves = calculatePawnMoves(board, index);
      break;
    case "r":
      legalMoves = calculateRookMoves(board, index);
      break;
    case "n":
      legalMoves = calculateKnightMoves(board, index);
      break;
    case "b":
      legalMoves = calculateBishopMoves(board, index);
      break;
    case "q":
      legalMoves = calculateQueenMoves(board, index);
      break;
    case "k":
      legalMoves = calculateKingMoves(board, index);
      break;
  }
  return legalMoves;
};

function isOccupied(board, index) {
  return board[index] !== 0;
}

function isSameColor(board, index1, index2) {
  return (
    board[index1] & board[index2] & 0b10000 ||
    board[index1] & board[index2] & 0b1000
  );
}

function calculatePawnMoves(board, index) {
  let rank = Math.floor(index / 8);
  let file = index % 8;
  const legalMoves = [];
  const color = board[index] & 0b10000 ? "w" : "b";
  if (color === "b") {
    if(!isOccupied(board, index - 8)) legalMoves.push(index - 8);
    if (rank === 6 && !isOccupied(board, index - 16)) legalMoves.push(index - 16);
    if (file > 0 && isOccupied(board, index - 7) && !isSameColor(board, index, index - 7)) legalMoves.push(index - 7);
    if (file < 7 && isOccupied(board, index - 9) && !isSameColor(board, index, index - 9)) legalMoves.push(index - 9);
  } else {
    if(!isOccupied(board, index + 8)) legalMoves.push(index + 8);
    if (rank === 1 && !isOccupied(board, index + 16)) legalMoves.push(index + 16);
    if (file > 0 && isOccupied(board, index + 9) && !isSameColor(board, index, index + 9)) legalMoves.push(index + 9);
    if (file < 7 && isOccupied(board, index + 7) && !isSameColor(board, index, index + 7)) legalMoves.push(index + 7);
  }
  return legalMoves;

}

function calculateRookMoves(board, index) {
  const legalMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  for (let i = -1; i >= -file; i--) {
    if (isOccupied(board, index + i)) {
      if (!isSameColor(board, index, index + i)) legalMoves.push(index + i);
      break;
    }
    legalMoves.push(index + i);
  }
  for (let i = 1; i < 8 - file; i++) {
    if (isOccupied(board, index + i)) {
      if (!isSameColor(board, index, index + i)) legalMoves.push(index + i);
      break;
    }
    legalMoves.push(index + i);
  }
  for (let i = -1; i >= -rank; i--) {
    if (isOccupied(board, index + 8 * i)) {
      if (!isSameColor(board, index, index + 8 * i))
        legalMoves.push(index + 8 * i);
      break;
    }
    legalMoves.push(index + 8 * i);
  }
  for (let i = 1; i < 8 - rank; i++) {
    if (isOccupied(board, index + 8 * i)) {
      if (!isSameColor(board, index, index + 8 * i))
        legalMoves.push(index + 8 * i);
      break;
    }
    legalMoves.push(index + 8 * i);
  }
  return legalMoves;
}

function calculateBishopMoves(board, index) {
  const legalMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  for (let i = -1; i >= -file; i--) {
    // up and left
    if (isOccupied(board, index + i + i * 8)) {
      if (!isSameColor(board, index, index + i + i * 8))
        legalMoves.push(index + i + i * 8);
      break;
    }
    legalMoves.push(index + i + i * 8);
  }
  for (let i = 1; i < 8 - file; i++) {
    //down and right
    if (isOccupied(board, index + i + i * 8)) {
      if (!isSameColor(board, index, index + i + i * 8))
        legalMoves.push(index + i + i * 8);
      break;
    }
    legalMoves.push(index + i + i * 8);
  }
  for (let i = -1; i >= -rank; i--) {
    //up and right
    if (isOccupied(board, index + 8 * i - i)) {
      if (!isSameColor(board, index, index + 8 * i - i))
        legalMoves.push(index + 8 * i - i);
      break;
    }
    legalMoves.push(index - i + 8 * i);
  }
  for (let i = 1; i < 8 - rank; i++) {
    //down and left
    if (isOccupied(board, index + 8 * i - i)) {
      if (!isSameColor(board, index, index + 8 * i - i))
        legalMoves.push(index + 8 * i - i);
      break;
    }

    legalMoves.push(index + 8 * i - i);
  }
  return legalMoves;
}

function calculateKnightMoves(board, index) {
  return [];
}


function calculateQueenMoves(board, index){
  return [...calculateBishopMoves(board, index), ...calculateRookMoves(board, index)];
}

function calculateKingMoves(board, index){
  let legalMoves = [];
  const rank = Math.floor(index / 8);
  const file = index % 8;
  if (file > 0) { 
    legalMoves.push(index - 1);
    if (rank > 0) legalMoves.push(index - 9);
    if (rank < 7) legalMoves.push(index + 7);
  }
  if (file < 7) {
    legalMoves.push(index + 1);
    if (rank > 0) legalMoves.push(index - 7);
    if (rank < 7) legalMoves.push(index + 9);
  }
  if (rank > 0) legalMoves.push(index - 8);
  if (rank < 7) legalMoves.push(index + 8);
  return legalMoves;

}
