import "./App.css";
import { useState } from "react";
import Box from "@mui/material/Box";
// import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import CssBaseline from "@mui/material/CssBaseline";


const pieces = {
  king: "♔",
  queen: "♕",
  rook: "♖",
  bishop: "♗",
  knight: "♘",
  pawn: "♙",
};


function App() {
  const [FEN, setFEN] = useState("rnbqkbrn/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR"); 




  return (

      <Container >
      <CssBaseline />

      <Grid container>
        {Array.from(new Array(8)).map((_, i) => (
          <Grid key={i} container item>
            {Array.from(new Array(8)).map((_, j) => (
              <Grid
                component={Box}
                item
                key={j}
                sx={{
                  bgcolor: (i + j) % 2 ? "aaaaaa" : "#333333",
                  height: "75px",
                  width: "75px",
                  position:"relative",
                }}
              >
                <Piece type = {[FEN.split('/')[i][j]]}>
                {
                  {

                  }[FEN.split('/')[i][j]]
                }
                </Piece>
              </Grid> 
            ))}
          </Grid>
        ))}
      </Grid>
      </Container>

  );
}

export default App;

function Piece (type){
  return (
    {
      k: <King />,
      K: <King />,
      q: <Queen />,
      Q: <Queen />,

      r: <Rook />,
      R: <Rook />,
      b: <Bishop />,
      B: <Bishop />,
      n: <Knight />,
      N: <Knight />,
      p: <Pawn />,
      P: <Pawn />,

    }[type]
  )
}

function Knight () {
  return <span>{pieces.knight}</span>
}

function Bishop () {
  return <span>{pieces.bishop}</span>
}

function Rook () {
  return <span>{pieces.rook}</span>
}

function Queen () {
  return <span>{pieces.queen}</span>
}

function King () {
  return <span>{pieces.king}</span>
}
function Pawn () {
  return <span>{pieces.pawn}</span>
}
