import { useState,useEffect } from "react";

import pieces from "./pieces";

export default function Piece({
  piece: initial,
  setActivePiece,
  index,
  activePiece,
  setBoard,
  board
}) {
  const [color, setColor] = useState(0b10000 & initial ? "w" : "b");
  const [type, setType] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [offset, setOffset] = useState({ x: 0, y: 0 });



  useEffect(() => { 
    setColor(0b10000 & initial ? "w" : "b")
    setType(Object.keys(pieces).find((key) => pieces[key] === initial));
  }, [initial])

  const handleMouseDown = (event) => {
    event.preventDefault();
    setActivePiece(index);
    setIsDragging(true);
    const piece = event.target.getBoundingClientRect();
    setOffset({
      x: piece.left + piece.width/2 ,
      y: piece.top  + piece.height/2,
    });
    console.log(calculateLegalMoves(board, index))

  };

  const handleMouseMove = (event) => {
    if (!isDragging) return;
    
    setPosition({
      x: (-offset.x + event.clientX ) ,
      y: (- offset.y + event.clientY ),
    });
    const x = event.clientX;
    const y = event.clientY;
    const board = document.getElementById("board").getBoundingClientRect();
    const rank = Math.floor((y - board.top) / 70);
    const file = Math.floor((x - board.left) / 70);
    if (rank < 0 || rank > 7 || file < 0 || file > 7) {
      // setPosition({ x: 0, y: 0 });
      return;
    };
    const squares = document.getElementsByClassName("square");

    squares[rank * 8 + file].classList.add("dragover");
  };

  const handleMouseUp = (event) => {
    // event.target.style.cursor= "default"
    setIsDragging(false);



    const x = event.clientX;
    const y = event.clientY;
    const boardRect = document.getElementById("board").getBoundingClientRect();
    const rank = Math.floor((y - boardRect.top) / 70);
    const file = Math.floor((x - boardRect.left) / 70);
    const newElement = initial;

    if (rank < 0 || rank > 7 || file < 0 || file > 7) {
      setPosition({ x: 0, y: 0 });
      return;
    };
    if(rank * 8 + file === index) {
      setPosition({ x: 0, y: 0 });
      return;

    }
    setBoard((prev) => {
      return prev.map((element, i) => {
        if(index === i) return 0;
        if (i === rank * 8 + file) {
          return newElement;
        }
        return element;
      })
    });
  };

  // const handleDragStart = (event) => {
  //   event.dataTransfer.effect = "none";
  // }
  return (
    <img
      style={{ left: position.x, top: position.y }}
      className={"piece" + (activePiece === index ? " active" : "")}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      // onDragStart = {handleDragStart}
      src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
      alt="Chess Piece"
    />
  );
}


const calculateLegalMoves = (board, index) => {
  let legalMoves = [];

  const piece = board[index];
  const color = piece & 0b10000 ? "w" : "b";

  const type = Object.keys(pieces).find((key) => pieces[key] === piece);

  switch (type.toLowerCase()) {
    case "p":
      return legalMoves.push(index + (color === "w" ? -8 : 8));
    case "r":
      for (let i = 1; i < 8; i++) {
        return legalMoves.push(index + i * 8);

      }


      }
      return legalMoves;
}
