import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

import decodeFEN from "./FENtoBoard";

import useAction, { setBoardAction } from "./actions";
import { selectBoard, selectFEN } from "./slices/game";

import Grid from "@mui/material/Grid";
import Square from "./Square";


const ChessBoard = () => {

  const FEN = useSelector(selectFEN);
  const setBoard = useAction(setBoardAction);

  useEffect(() => {
    // setBoard([...decodeFEN(FEN)[0]]);
    console.log(decodeFEN(FEN));
  }, [FEN, setBoard]);

  return (
    <Grid container spacing={0} id="board" draggable="false">
      {Array.from(new Array(8)).map((_, rank) => (
        <Grid key={`${rank}`} container item xs={12} spacing={0}>
          {Array.from(new Array(8)).map((_, file) => (
            <Grid key={`${file}`} item xs={1}>
              <Square index={rank * 8 + file} />
            </Grid>
          ))}
        </Grid>
      ))}
    </Grid>
  );
};

export default ChessBoard;
