import Paper from "@mui/material/Paper";
import Piece from "./Piece";

import { useSelector } from "react-redux";
import { selectBoard, selectLegalMoves } from "./slices/game";

const Square = ({ rank, file }) => {
  const board = useSelector(selectBoard);
  const legalMoves = useSelector(selectLegalMoves);
  console.log(legalMoves)
  return (
    <Paper
      draggable="false"
      className= {"square" + legalMoves.includes(rank * 8 + file) ? " legal" : ""}
      style={{
        position: "relative",
        height: "70px",
        width: "70px",
        backgroundColor: (rank + file) % 2 ? "#eeeed2" : "#769656",
      }}
    >
      {board[rank * 8 + file] !== 0 && (
        <Piece piece={board[rank * 8 + file]} index={rank * 8 + file} />
      )}
    </Paper>
  );
};

export default Square;
