import { useState } from "react";

import pieces from "./pieces";

const startingCoordinates =  {x: null, y: null}

export default function Piece({
  piece: initial,
  setActivePiece,
  index,
  activePiece,
}) {
  const [color] = useState(initial > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );

  const handleDragStart = (ev) => {
    setActivePiece(index);
    startingCoordinates.x = ev.screenX;
    startingCoordinates.y = ev.screenY;
    var img = new Image();
    img.src = 'data:image/gif;base64,R0lGODlhAQABAIAAAAUEBAAAACwAAAAAAQABAAACAkQBADs=';
    ev.dataTransfer.setDragImage(img, 0, 0);
    ev.dataTransfer.effectAllowed = "move";
  };
  const handleDrag = (ev) => {
    ev.target.style.transform = `translate(${ ev.screenX -startingCoordinates.x - (ev.target.width/2) }px, ${ev.screenY -startingCoordinates.y -(ev.target.height/2) }px)`;
  }

  const handleDragEnd = (ev) => {
    console.log(ev);
    ev.target.style.transform = `translate(-50%, -50%)`;
  }
  return (
      <img
        className={"piece" + (activePiece === index ? " active" : "")}
        onDragStart={handleDragStart}
        onDrag = {handleDrag}
        onDragEnd = {handleDragEnd}
        src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
        alt="Chess Piece"
      />
  );
}
