import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";

const ChessBoard = () => {

    const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");
    const [activePiece, setActivePiece] = useState(null);
  
  const chessboard = [...Array(64)].map((_, index) => ({
    row: Math.floor(index / 8),
    col: index % 8,
    color: (Math.floor(index / 8) + (index % 8)) % 2 === 0 ? "white" : "gray",
  }));

  return (
    <Grid container spacing={0}>
      {chessboard.map(({ row, col, color }) => (
        <Grid item key={`${row}-${col}`} xs={1}>
          <Paper style={{ height: "100%", backgroundColor: color }} >
            { 
                <Piece key={index} piece={piece} setActivePiece = {setActivePiece} activePiece= {activePiece} index={index}/>
}
            </Paper>
        </Grid>
      ))}
    </Grid>
  );
};

export default ChessBoard;

const FENtoBoard = (FEN) => {
    const board = Array.from(64).fill(0);
    let file = 0;
    let rank = 7;

    [...FEN].forEach((symbol) => {
      if (symbol == "/") {
        file = 0;
        rank--;
      } else {
        if (!isNaN(parseInt(symbol, 10))) {
          file += parseInt(symbol, 10);
        } else {
          board[rank * 8 + file] =  pieces[symbol];
          file++;
        }
      }
    });
    return board;
  };

  function Piece({piece, setActivePiece, index, activePiece}) {
    const [color ] = useState(piece > 6 ? "w" : "b");
    const [type] = useState(Object.keys(pieces).find(key => pieces[key] === piece));
  
    
    return <img className={"piece" + ((activePiece == index) ? " active" : "")} onMouseDown= {()=>setActivePiece(index)} src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}></img>;
  }
