import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import { useState } from "react";

import Piece from "./Piece";


const ChessBoard = () => {
  const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");
  const [activePiece, setActivePiece] = useState(null);
  const board = FENtoBoard(FEN);
  console.log(board);
  const chessboard = [...Array(64)].map((_, index) => ({
    row: Math.floor(index / 8),
    col: index % 8,
    color: (Math.floor(index / 8) + (index % 8)) % 2 === 0 ? "white" : "gray",
  }));

  return (
    <Grid container spacing={0}>
      {chessboard.map(({ rank, file, color }) => (
        <Grid item key={`${rank}-${file}`} xs={1}>
          <Paper style={{ height: "100%", backgroundColor: color }}>
            {board[rank * 8 + file] && (
              <Piece
                piece={board[rank * 8 + file]}
                setActivePiece={setActivePiece}
                activePiece={activePiece}
                index={rank * 8 + file}
              />
            )}
          </Paper>
        </Grid>
      ))}
    </Grid>
  );
};

export default ChessBoard;

const FENtoBoard = (FEN) => {
  const board = Array.from(64).fill(0);
  let file = 0;
  let rank = 7;

  [...FEN].forEach((symbol) => {
    if (symbol == "/") {
      file = 0;
      rank--;
    } else {
      if (!isNaN(parseInt(symbol, 10))) {
        file += parseInt(symbol, 10);
      } else {
        board[rank * 8 + file] = pieces[symbol];
        file++;
      }
    }
  });
  return board;
};
