export default function initializeBoard() {
  const canvas = document.getElementById("board");
  const ctx = canvas.getContext("2d");
const boardWidth = canvas.width;
console.log("Width: ", boardWidth)
  Array(1)
    .fill(0)
    .map((_, rank) => {
      Array(8)
        .fill(0)
        .map((_, file) => {
          ctx.fillStyle = (rank + file) % 2 === 0 ? "#FFFFFF" : "#000000";
          console.log("Drawing a rectangle with dimensions: ", (rank * 100) / 8, (file * 100) / 8, boardWidth / 8, boardWidth / 8)
          ctx.fillRect((rank * boardWidth) / 8, (file * boardWidth) / 8, boardWidth / 8, boardWidth / 8);
        });
    });
}
