import { useState } from "react";

import pieces from "./pieces";

export default function Piece({
  piece: initial,
  setActivePiece,
  index,
  activePiece,
}) {
  const [color] = useState(initial > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );

  const handleMouseDown = (ev) => {
    setActivePiece(index);
    const img = new Image();
    img.src = piece.src;
    //set the cursor to be a pointer when dragging
    ev.dataTransfer.effectAllowed = "move";
    ev.dataTransfer.setDragImage(img, 0, 0);
  };

  return (
    <img
      draggable
      className={"piece" + (activePiece === index ? " active" : "")}
      onDragStart={handleMouseDown}
      src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}
      alt="Chess Piece"
    />
  );
}
