import "./App.css";
import { useState } from "react";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import ChessBoard from "./ChessBoard";
// import pieceImages from "./pieceImages";


const pieces = {
  p : 1,
  n : 2,
  b : 3,
  r : 4,
  q : 5,
  k : 6,
  P : 7,
  N : 8,
  B : 9,
  R : 10,
  Q : 11,
  K : 12,
}


function App() {
  const [FEN] = useState("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR");
  const [activePiece, setActivePiece] = useState(null);

  const FENtoBoard = (FEN) => {
    const board = Array.from(64).fill(0);
    let file = 0;
    let rank = 7;

    [...FEN].forEach((symbol) => {
      if (symbol == "/") {
        file = 0;
        rank--;
      } else {
        if (!isNaN(parseInt(symbol, 10))) {
          file += parseInt(symbol, 10);
        } else {
          board[rank * 8 + file] =  pieces[symbol];
          file++;
        }
      }
    });
    return board;
  };

  return (
    <Container>
      <ChessBoard />
      <CssBaseline />
      {
        <div className="board">
          {FENtoBoard(FEN).map((piece, index) => {
            return  <> <Piece key={index} piece={piece} setActivePiece = {setActivePiece} activePiece= {activePiece} index={index}/> { ((index+1) % 8 ==0) && <br /> } </>;
          }
          )}
        </div>
      }
    </Container>
  );
}

export default App;

function Piece({piece, setActivePiece, index, activePiece}) {
  const [color ] = useState(piece > 6 ? "w" : "b");
  const [type] = useState(Object.keys(pieces).find(key => pieces[key] === piece));


  return <img className={"piece" + ((activePiece == index) ? " active" : "")} onMouseDown= {()=>setActivePiece(index)} src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}></img>;
}
