import { useState } from "react";

import pieces from "./pieces";

export default function Piece({ piece:initial, setActivePiece, index, activePiece }) {
  const [color] = useState(initial > 6 ? "w" : "b");
  const [type] = useState(
    Object.keys(pieces).find((key) => pieces[key] === initial)
  );

  const piece = <img draggable className={"piece" + (activePiece === index ? " active" : "")} onDragStart={handleDragStart} src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`} alt= "Chess Piece" />;

  const handleDragStart = (ev) => {

    setActivePiece(index);

    const img = new Image();
    img.src = "pieces/bB.svg";
    ev.dataTransfer.setDragImage(piece, 0, 0);
  };




  return piece;
}
