import { useEffect } from "react";

export default function Pieces() {
  useEffect(() => {
    const spriteSheet = new Image();
    spriteSheet.src = "pieces.jpg"; // Replace with your image path
    spriteSheet.onload = extractChessPieces;
    console.log(spriteSheet);

    function extractChessPieces() {
      const canvas = document.getElementById("canvas");
      console.log(canvas);
      const ctx = canvas.getContext("2d");

      const pieceWidth = spriteSheet.width/4.0; // Adjust the dimensions to match your piece size
      const pieceHeight = spriteSheet.height/2.0;

      canvas.width = pieceWidth;
      canvas.height = pieceHeight;

      const pieces = ["white-king", "black-king", "white-queen", "black-queen", "white-pawn", "white-bishop", ]; // Add other piece names

      pieces.forEach((piece, index) => {
        const posX = (index * pieceWidth) % (spriteSheet.width / 2.0);
        const posY =
          Math.floor((index * pieceWidth) / spriteSheet.width) * pieceHeight;

          console.log(index, piece, posX, posY, pieceWidth);

        ctx.clearRect(0, 0, pieceWidth, pieceHeight);
        ctx.drawImage(
          spriteSheet,
          posX,
          posY,
          pieceWidth,
          pieceHeight,
          0,
          0,
          pieceWidth,
          pieceHeight
        );

        const dataURL = canvas.toDataURL();

        const img = new Image();
        img.src = dataURL;
        img.alt = piece;

        document.body.appendChild(img); // Add the image to the desired location in your HTML
        console.log(img);
      });
    }
  }, []);
  return (
    <div>
      <canvas id="canvas"></canvas>
     
    </div>
  );
}
