import "./App.css";
import { useState } from "react";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import ChessBoard from "./ChessBoard";
// import pieceImages from "./pieceImages";


const pieces = {
  p : 1,
  n : 2,
  b : 3,
  r : 4,
  q : 5,
  k : 6,
  P : 7,
  N : 8,
  B : 9,
  R : 10,
  Q : 11,
  K : 12,
}


function App() {


  return (
    <Container>
      <CssBaseline />
      <ChessBoard />
    </Container>
  );
}

export default App;

function Piece({piece, setActivePiece, index, activePiece}) {
  const [color ] = useState(piece > 6 ? "w" : "b");
  const [type] = useState(Object.keys(pieces).find(key => pieces[key] === piece));


  return <img className={"piece" + ((activePiece == index) ? " active" : "")} onMouseDown= {()=>setActivePiece(index)} src={`pieces/${color.toLowerCase()}${type.toUpperCase()}.svg`}></img>;
}
