export default function initializeBoard() {
  const canvas = document.getElementById("board");
  const ctx = canvas.getContext("2d");
const boardWidth = canvas.width;
  Array(8)
    .fill(0)
    .map((_, rank) => {
      Array(8)
        .fill(0)
        .map((_, file) => {
          ctx.fillStyle = (rank + file) % 2 === 0 ? "#FFFFFF" : "#000000";
          ctx.fillRect((rank * boardWidth) / 8, (file * boardWidth) / 8, boardWidth / 8, boardWidth / 8);
        });
    });
}


export function drawBoard(FEN){
    const canvas = document.getElementById("board");
    const ctx = canvas.getContext("2d");
    const boardWidth = canvas.width;
    const boardHeight = canvas.height;
    const board = FEN.split(" ")[0];
    const ranks = board.split("/");
    console.log("Ranks: ", ranks)
    ranks.map((rank, rankIndex) => {
        let fileIndex = 0;
        for (let i = 0; i < rank.length; i++) {
            const char = rank[i];
            if (isNaN(char)) {
                const piece = char;
                const img = new Image();
                img.onload = () => {
                    ctx.drawImage(img, (fileIndex * boardWidth) / 8, (rankIndex * boardHeight) / 8, boardWidth / 8, boardHeight / 8);
                };
                console.log(piece);
                img.src = `/assets/${piece}.png`;
                fileIndex++;
            } else {
                fileIndex += parseInt(char);
            }
        }
    });
}
