import Paper from "@mui/material/Paper";
import Piece from "./Piece";

const Square = ({ rank, file, board, setBoard, activePiece, setActivePiece }) => {

  return (
    <Paper
  className="square"
      style={{
        position: "relative",
        height: "70px",
        width: "70px",
        backgroundColor: (rank + file) % 2 ? "#eeeed2" : "#769656",
      }}
    >
      {board[rank * 8 + file] !== 0 && (
        <Piece
          piece={board[rank * 8 + file]}
          setActivePiece={setActivePiece}
          activePiece={activePiece}
          index={rank * 8 + file}
          setBoard = {setBoard}
          board = {board}
        />
      )}
    
    </Paper>
  );
};

export default Square;
