export default function Pieces() {

const spriteSheet = new Image();
spriteSheet.src = '/pieces.jpg'; // Replace with your image path
spriteSheet.onload = extractChessPieces;

function extractChessPieces() {
  const canvas = document.getElementById('canvas');
  console.log(canvas)
  const ctx = canvas.getContext('2d');
  
  const pieceWidth = 50; // Adjust the dimensions to match your piece size
  const pieceHeight = 50;
  
  canvas.width = pieceWidth;
  canvas.height = pieceHeight;
  
  const pieces = ['white-king', 'black-king', 'white-queen', 'black-queen']; // Add other piece names
  
  pieces.forEach((piece, index) => {
    const posX = (index * pieceWidth) % spriteSheet.width;
    const posY = Math.floor((index * pieceWidth) / spriteSheet.width) * pieceHeight;
    
    ctx.clearRect(0, 0, pieceWidth, pieceHeight);
    ctx.drawImage(spriteSheet, posX, posY, pieceWidth, pieceHeight, 0, 0, pieceWidth, pieceHeight);
    
    const dataURL = canvas.toDataURL();
    
    const img = new Image();
    img.src = dataURL;
    img.alt = piece;
    
    document.body.appendChild(img); // Add the image to the desired location in your HTML
  });
}
}
