  const FENtoBoard = (FEN) => {
    const board = [];
    const rows = FEN.split("/");
    console.log(rows);
    for (let i = 0; i < rows.length; i++) {
      for (let j = 0; j < rows[i].length; j++) {
        console.log(rows[i][j])
        if(!isNaN(parseInt(rows[i][j], 10))){
          j += parseInt(rows[i][j], 10);
          console.log("found int, adding " + parseInt(rows[i][j]) + " to j")
        }
      }
    }
    return board;
  };

  console.log(FENtoBoard(FEN));

  return (
    <Container>
      <CssBaseline />
    </Container>
  );
}

export default App;

